pragma solidity >0.4.99 <0.9.0;

contract Son {
    uint public x;
    constructor(uint a) public payable {
        x = a;
    }
}

contract Parent {
    Son son1 = new Son(4); // will be executed as part of C's constructor
//    Son son2 = new Son(4); // will be executed as part of C's constructor
//    Son son3 = new Son(4); // will be executed as part of C's constructor
//    Son son4 = new Son(4); // will be executed as part of C's constructor

    function createSon(uint arg) public payable {
        Son newSon = new Son(arg);
        require( newSon.x() == arg, "===NOT EQUAL===");
    }

}
